import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def collision(x, y, vx, vy, r):
    return x**2 + y**2 - r**2

def reflect(x, y, vx, vy):
    angle = math.atan2(vy, vx)
    return -vx * math.cos(2 * angle) - vy * math.sin(2 * angle), -vx * math.sin(2 * angle) + vy * math.cos(2 * angle)

def simulate(px, py, vx, vy, d):
    x, y = px, py
    dist = 0
    while True:
        if collision(x, y, 0, 0, 1) <= 0:
            return dist
        dist += math.sqrt(vx**2 + vy**2) * 1e-6
        x += vx * 1e-6
        y += vy * 1e-6
        vx, vy = reflect(x, y, vx, vy)

def main():
    while True:
        d = float(input())
        if d == 0:
            break
        px, py, vx, vy = map(float, input().split())
        dist = simulate(px, py, vx, vy, d)
        if dist > d:
            print("impossible")
        else:
            print(f"{dist:.10f}")

if __name__ == "__main__":
    main()
