#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Button {
    int x1, y1, x2, y2;
    string name;
};

struct Page {
    string name;
    vector<Button> buttons;
};

int main() {
    int n;
    while (cin >> n && n) {
        int W, H;
        cin >> W >> H;

        vector<Page> pages(n);
        for (int i = 0; i < n; ++i) {
            string name;
            int b;
            cin >> name >> b;
            pages[i].name = name;
            for (int j = 0; j < b; ++j) {
                Button button;
                cin >> button.x1 >> button.y1 >> button.x2 >> button.y2;
                cin >> button.name;
                pages[i].buttons.push_back(button);
            }
        }

        int m;
        cin >> m;
        vector<string> history;
        history.push_back(pages[0].name);
        for (int i = 0; i < m; ++i) {
            string op;
            cin >> op;
            if (op == "show") {
                cout << history.back() << endl;
            } else if (op == "back") {
                if (!history.empty()) {
                    history.pop_back();
                }
            } else if (op == "forward") {
                if (!history.empty()) {
                    history.push_back(history.back());
                    history.pop_back();
                }
            } else {
                int x, y;
                cin >> x >> y;
                for (int j = 0; j < pages.back().buttons.size(); ++j) {
                    Button button = pages.back().buttons[j];
                    if (button.x1 <= x && x <= button.x2 && button.y1 <= y && y <= button.y2) {
                        history.push_back(button.name);
                        break;
                    }
                }
            }
        }
    }
    return 0;
}
