#include <stdio.h>
#include <stdlib.h>

#define MOD 10007
#define MAXN 100005

typedef struct {
    int b[2];
    int f[2];
} Edge;

int n;
Edge e[MAXN];
int d[MAXN];
int cnt;
int ans;

void dfs(int u) {
    d[u] = cnt;
    for (int i = 0; i < 2; i++) {
        if (e[u].b[i] != -1 && d[e[u].b[i]] != cnt) {
            dfs(e[u].b[i]);
        }
    }
}

void dfs2(int u) {
    d[u] = cnt;
    for (int i = 0; i < 2; i++) {
        if (e[u].b[i] != -1 && d[e[u].b[i]] != cnt) {
            dfs2(e[u].b[i]);
        }
    }
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d", &n);
        for (int i = 1; i <= n; i++) {
            e[i].b[0] = -1;
            e[i].b[1] = -1;
            e[i].f[0] = 0;
            e[i].f[1] = 0;
        }
        for (int i = 1; i <= n; i++) {
            int a, b, c, d;
            scanf("%d%d%d%d", &a, &b, &c, &d);
            e[i].b[0] = a;
            e[i].f[0] = b;
            e[i].b[1] = c;
            e[i].f[1] = d;
        }
        ans = 0;
        for (int i = 1; i <= n; i++) {
            if (d[i] == 0) {
                cnt++;
                dfs(i);
            }
        }
        for (int i = 1; i <= n; i++) {
            if (d[i] == cnt) {
                for (int j = 0; j < 2; j++) {
                    if (e[i].b[j] != -1 && d[e[i].b[j]] != cnt) {
                        dfs2(e[i].b[j]);
                    }
                }
            }
        }
        for (int i = 1; i <= n; i++) {
            if (d[i] == cnt) {
                int sum = 0;
                for (int j = 0; j < 2; j++) {
                    if (e[i].b[j] != -1 && d[e[i].b[j]] == cnt) {
                        sum += e[i].f[j];
                    }
                }
                ans = (ans + sum) % MOD;
            }
        }
        printf("%d\n", ans);
    }
    return 0;
}
