#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MOD = 10007;

struct Edge {
    int to, cost;
};

struct UnionFind {
    vector<int> parent;
    vector<int> size;

    UnionFind(int n) : parent(n), size(n, 1) {
        for (int i = 0; i < n; i++) {
            parent[i] = i;
        }
    }

    int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    bool unite(int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) {
            return false;
        }
        if (size[x] < size[y]) {
            swap(x, y);
        }
        parent[y] = x;
        size[x] += size[y];
        return true;
    }

    int getSize(int x) {
        return size[find(x)];
    }
};

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) {
            break;
        }
        vector<vector<Edge>> graph(n);
        for (int i = 0; i < n; i++) {
            int b0, f0, b1, f1;
            cin >> b0 >> f0 >> b1 >> f1;
            graph[i].push_back({b0, f0});
            graph[i].push_back({b1, f1});
        }

        vector<int> deg(n);
        for (int i = 0; i < n; i++) {
            for (const auto& e : graph[i]) {
                deg[e.to]++;
            }
        }

        vector<int> order;
        for (int i = 0; i < n; i++) {
            if (deg[i] == 0) {
                order.push_back(i);
            }
        }

        UnionFind uf(n);
        vector<int> used(n);
        int ans = 1;
        for (int i = 0; i < order.size(); i++) {
            int v = order[i];
            for (const auto& e : graph[v]) {
                if (used[e.to]) {
                    continue;
                }
                used[e.to] = 1;
                if (uf.unite(v, e.to)) {
                    ans = (ans * 2) % MOD;
                }
            }
        }

        cout << ans << endl;
    }
    return 0;
}
