import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x2 - x1)**2 + (y2 - y1)**2)

def is_point_in_triangle(x1, y1, x2, y2, x3, y3, xp, yp):
    def is_point_on_segment(x1, y1, x2, y2, xp, yp):
        return (xp - x1) * (y2 - y1) - (yp - y1) * (x2 - x1) <= 0

    def is_point_inside_triangle(x1, y1, x2, y2, x3, y3, xp, yp):
        return (is_point_on_segment(x1, y1, x2, y2, xp, yp) and
                is_point_on_segment(x2, y2, x3, y3, xp, yp) and
                is_point_on_segment(x3, y3, x1, y1, xp, yp))

    def is_point_on_triangle_edge(x1, y1, x2, y2, x3, y3, xp, yp):
        return (is_point_on_segment(x1, y1, x2, y2, xp, yp) or
                is_point_on_segment(x2, y2, x3, y3, xp, yp) or
                is_point_on_segment(x3, y3, x1, y1, xp, yp))

    def is_point_close_to_edge(x1, y1, x2, y2, x3, y3, xp, yp):
        return (distance(xp, yp, x1, y1) + distance(x1, y1, x2, y2) - distance(x2, y2, x3, y3) <= 0.001 or
                distance(xp, yp, x2, y2) + distance(x2, y2, x3, y3) - distance(x3, y3, x1, y1) <= 0.001 or
                distance(xp, yp, x3, y3) + distance(x3, y3, x1, y1) - distance(x1, y1, x2, y2) <= 0.001)

    return (is_point_inside_triangle(x1, y1, x2, y2, x3, y3, xp, yp) or
            is_point_close_to_edge(x1, y1, x2, y2, x3, y3, xp, yp))

def main():
    while True:
        try:
            x1, y1, x2, y2, x3, y3, xp, yp = map(float, input().split())
            if is_point_in_triangle(x1, y1, x2, y2, x3, y3, xp, yp):
                print("YES")
            else:
                print("NO")
        except ValueError:
            break

if __name__ == "__main__":
    main()
