import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 10007;
    static int n;
    static int[][] g;
    static int[] cnt;
    static int[] dp;
    static int[] vis;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            n = Integer.parseInt(br.readLine());
            g = new int[n][2];
            cnt = new int[n];
            dp = new int[n];
            vis = new int[n];
            for (int i = 0; i < n; i++) {
                StringTokenizer st = new StringTokenizer(br.readLine());
                for (int j = 0; j < 2; j++) {
                    g[i][j] = Integer.parseInt(st.nextToken());
                    g[g[i][j]][j] = i;
                }
                cnt[i] = 2;
            }
            for (int i = 0; i < n; i++) {
                if (vis[i] == 0) {
                    dfs(i);
                }
            }
            long ans = 0;
            for (int i = 0; i < n; i++) {
                if (dp[i] == 0) {
                    ans += dfs2(i);
                }
            }
            pw.println(ans % MOD);
        }
        pw.close();
    }

    static void dfs(int u) {
        vis[u] = 1;
        for (int i = 0; i < 2; i++) {
            if (g[u][i] != -1 && vis[g[u][i]] == 0) {
                dfs(g[u][i]);
            }
        }
    }

    static long dfs2(int u) {
        if (dp[u] != 0) {
            return dp[u];
        }
        long ans = 1;
        for (int i = 0; i < 2; i++) {
            if (g[u][i] != -1 && cnt[g[u][i]] > 0) {
                cnt[g[u][i]]--;
                ans = (ans * dfs2(g[u][i])) % MOD;
                cnt[g[u][i]]++;
            }
        }
        dp[u] = (int) ans;
        return dp[u];
    }
}
