from collections import defaultdict
import sys

MOD = 10007

def dfs(u, p, g, dp, vis):
    vis[u] = 1
    for v, w in g[u]:
        if v == p:
            continue
        if vis[v] == 1:
            dp[u] = 1
        elif vis[v] == 0:
            dfs(v, u, g, dp, vis)
            if dp[v] == 1:
                dp[u] = 1

def solve():
    n = int(input())
    if n == 0:
        return
    g = defaultdict(list)
    for _ in range(n):
        u, v, w, x, y, z = map(int, input().split())
        g[u].append((v, w))
        g[u].append((y, z))
        g[v].append((u, w))
        g[y].append((u, z))
    dp = [0] * (n + 1)
    vis = [0] * (n + 1)
    ans = 0
    for i in range(1, n + 1):
        if vis[i] == 0:
            dfs(i, 0, g, dp, vis)
            if dp[i] == 1:
                ans += 1
    print(pow(2, ans, MOD))

t = 1
while True:
    solve()
    t -= 1
    if t == 0:
        break
