#include <stdio.h>
#include <stdlib.h>

#define MAX_H 11
#define MAX_W 11

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max(int a, int b) {
    return (a > b) ? a : b;
}

int penalty(int h, int w, int grid[MAX_H][MAX_W], int start[2], int end[2], int die[3]) {
    int i, j, penalty = 0;
    int x, y;

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            x = start[0] + i;
            y = start[1] + j;
            if (x >= 0 && x < h && y >= 0 && y < w) {
                penalty += grid[x][y] * die[(i + j) % 4 + 1];
            }
        }
    }

    x = end[0] - start[0];
    y = end[1] - start[1];
    if (x < 0) x = -x;
    if (y < 0) y = -y;
    penalty += x * die[0] + y * die[2];

    return penalty;
}

int main() {
    int h, w, i, j, start[2], end[2];
    int grid[MAX_H][MAX_W];
    int die[3] = {1, 2, 3};
    int min_penalty = 0;

    while (scanf("%d %d", &h, &w) == 2) {
        if (h == 0 && w == 0) break;

        for (i = 0; i < h; i++) {
            for (j = 0; j < w; j++) {
                scanf("%d", &grid[i][j]);
            }
        }

        scanf("%d %d", &start[0], &start[1]);
        scanf("%d %d", &end[0], &end[1]);

        min_penalty = 1000000;
        for (i = 0; i < 6; i++) {
            die[0] = (i / 2) % 3 + 1;
            die[1] = (i / 2) % 3 + 2;
            die[2] = (i % 2) * 2 + 4;
            min_penalty = min(min_penalty, penalty(h, w, grid, start, end, die));
        }

        printf("%d\n", min_penalty);
    }

    return 0;
}
