#include <iostream>
#include <vector>
#include <algorithm>
#include <climits>

using namespace std;

const int INF = INT_MAX;

int dp(vector<vector<int>>& grid, int h, int w, int x, int y, int dx, int dy, int penalty) {
    if (x < 0 || x >= h || y < 0 || y >= w) return INF;
    if (x == h - 1 && y == w - 1) return penalty;
    int& res = grid[x][y];
    if (res != -1) return res;
    res = INF;
    for (int i = 0; i < 6; ++i) {
        int nx = x + dx[i], ny = y + dy[i];
        res = min(res, dp(grid, h, w, nx, ny, dx[i], dy[i], penalty + grid[nx][ny] * (i % 3 + 1)));
    }
    return res;
}

int main() {
    int h, w;
    while (cin >> h >> w) {
        if (h == 0 && w == 0) break;
        vector<vector<int>> grid(h, vector<int>(w));
        for (int i = 0; i < h; ++i) {
            for (int j = 0; j < w; ++j) {
                cin >> grid[i][j];
            }
        }
        int sx, sy, ex, ey;
        cin >> sx >> sy >> ex >> ey;
        vector<vector<int>> dp_grid(h, vector<int>(w, -1));
        cout << dp(dp_grid, h, w, sx, sy, {0, 0, 0, 0, 1, -1}, {0, 1, -1, 1, 0, 0}, 0) << endl;
    }
    return 0;
}
