#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 110

typedef struct {
    int v, w;
} Edge;

typedef struct {
    int n, m, p;
    Edge e[MAXN * MAXN];
    int head[MAXN], edge_num;
    double dis[MAXN];
    int pre[MAXN];
} Graph;

void add_edge(Graph *g, int u, int v, int w) {
    g->e[++g->edge_num] = (Edge) {v, w};
    g->head[u] = g->edge_num;
}

void dijkstra(Graph *g) {
    int i, j, k;
    for (i = 0; i < g->n; i++) {
        for (j = 0; j <= g->n; j++) {
            g->dis[j] = 1e9;
            g->pre[j] = -1;
        }
        g->dis[i] = 0;
        for (k = 1; k < g->n; k++) {
            int u = -1, w = 1e9;
            for (j = 0; j < g->n; j++) {
                if (g->dis[j] < w) {
                    u = j;
                    w = g->dis[j];
                }
            }
            if (u == -1) break;
            g->dis[u] = w;
            for (j = g->head[u]; j; j = g->e[j].v) {
                int v = g->e[j].u;
                if (g->dis[v] > g->dis[u] + g->e[j].w) {
                    g->dis[v] = g->dis[u] + g->e[j].w;
                    g->pre[v] = u;
                }
            }
        }
    }
}

int main() {
    int n, m, p, i, j, k;
    double ans;
    while (scanf("%d%d%d", &n, &m, &p) == 3) {
        if (n == 0 && m == 0 && p == 0) break;
        Graph g;
        g.n = n;
        g.m = m;
        g.p = p;
        g.edge_num = 0;
        for (i = 1; i <= n; i++) {
            g.head[i] = 0;
        }
        for (i = 1; i <= m; i++) {
            scanf("%d%d%d", &j, &k, &g.e[i].w);
            g.e[i].u = j;
            g.e[i].v = k;
            add_edge(&g, j, k, g.e[i].w);
            add_edge(&g, k, j, g.e[i].w);
        }
        dijkstra(&g);
        for (i = 1; i <= p; i++) {
            scanf("%d", &j);
            ans = 0;
            for (k = g.pre[j]; k != -1; k = g.pre[k]) {
                ans += 1.0 / g.dis[k];
            }
            printf("%.6lf\n", ans / g.dis[j]);
        }
        printf("\n");
    }
    return 0;
}
