#include <iostream>
#include <vector>
#include <queue>
#include <cmath>
#include <iomanip>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

const int MAXN = 105;
const double EPS = 1e-9;

int n, m, p;
vector<pii> adj[MAXN];
double dist[MAXN];
double prob[MAXN];

void dijkstra() {
    priority_queue<pii, vector<pii>, greater<pii>> pq;
    pq.push({0, 0});
    dist[0] = 0;
    while (!pq.empty()) {
        int u = pq.top().second;
        double d = pq.top().first;
        pq.pop();
        if (d > dist[u] + EPS) continue;
        for (auto& [v, w] : adj[u]) {
            double nd = d + w;
            if (nd < dist[v] + EPS) {
                dist[v] = nd;
                pq.push({nd, v});
            }
        }
    }
}

int main() {
    while (cin >> n >> m >> p) {
        if (n == 0 && m == 0 && p == 0) break;
        for (int i = 0; i < n; i++) {
            adj[i].clear();
            dist[i] = 1e9;
        }
        for (int i = 0; i < m; i++) {
            int u, v, w;
            cin >> u >> v >> w;
            adj[u].push_back({v, w});
            adj[v].push_back({u, w});
        }
        dijkstra();
        for (int i = 0; i < p; i++) {
            int u;
            cin >> u;
            double prob = 0;
            for (int j = 0; j < n; j++) {
                if (dist[j] < dist[u] + EPS) {
                    prob += 1.0 / (n - 1);
                }
            }
            cout << fixed << setprecision(9) << prob << " ";
        }
        cout << "\n\n";
    }
    return 0;
}
