#include <stdio.h>
#include <stdlib.h>

#define MAX_N 5

int n;
int grid[MAX_N][MAX_N];

void print_grid() {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            printf("%d ", grid[i][j]);
        }
        printf("\n");
    }
}

void cast_spell(int x, int y, int size) {
    for (int i = x; i < x + size; i++) {
        for (int j = y; j < y + size; j++) {
            if (grid[i][j] == 1) {
                grid[i][j] = 0;
            } else if (grid[i][j] == 0) {
                grid[i][j] = 1;
            }
        }
    }
}

int is_win() {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (grid[i][j] == 1) {
                return 0;
            }
        }
    }
    return 1;
}

void solve() {
    int count = 0;
    for (int size = n; size > 0; size--) {
        for (int x = 0; x <= n - size; x++) {
            for (int y = 0; y <= n - size; y++) {
                cast_spell(x, y, size);
                if (is_win()) {
                    printf("myon");
                    count++;
                    return;
                }
                cast_spell(x, y, size);
            }
        }
    }
    printf("myon");
}

int main() {
    while (scanf("%d", &n) != EOF) {
        if (n == 0) {
            break;
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                scanf("%d", &grid[i][j]);
            }
        }
        solve();
        printf("\n");
    }
    return 0;
}
