#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int n;
    while (cin >> n && n != 0) {
        vector<vector<int>> enemies(n, vector<int>(n));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                cin >> enemies[i][j];
            }
        }

        string spell;
        while (true) {
            bool changed = false;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    if (enemies[i][j] == 1) {
                        int count = 0;
                        for (int x = max(0, i - 1); x <= min(n - 1, i + 1); x++) {
                            for (int y = max(0, j - 1); y <= min(n - 1, j + 1); y++) {
                                count += enemies[x][y];
                            }
                        }
                        if (count == 0) {
                            changed = true;
                            for (int x = max(0, i - 1); x <= min(n - 1, i + 1); x++) {
                                for (int y = max(0, j - 1); y <= min(n - 1, j + 1); y++) {
                                    enemies[x][y] = 1;
                                }
                            }
                        }
                    }
                }
            }
            if (!changed) break;
            spell += "myon";
        }
        cout << spell << endl;
    }
    return 0;
}
