import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextInt()) {
            int n = scanner.nextInt();
            if (n == 0) break;
            int[][] enemies = new int[n][n];
            for (int i = 0; i < n; i++) {
                String line = scanner.next();
                for (int j = 0; j < n; j++) {
                    enemies[i][j] = line.charAt(j) - '0';
                }
            }
            StringBuilder spell = new StringBuilder();
            while (true) {
                boolean changed = false;
                for (int i = 0; i < n; i++) {
                    for (int j = 0; j < n; j++) {
                        if (enemies[i][j] == 1) {
                            changed = true;
                            for (int x = Math.max(0, i - 100); x < Math.min(n, i + 101); x++) {
                                for (int y = Math.max(0, j - 100); y < Math.min(n, j + 101); y++) {
                                    enemies[x][y] = 1;
                                }
                            }
                        }
                    }
                }
                if (!changed) break;
                spell.append("myon");
            }
            System.out.println(spell.toString());
        }
    }
}
