#include <iostream>
#include <queue>
#include <vector>

int main() {
    std::queue<int> railTracks;
    int carNumber;

    while (std::cin >> carNumber) {
        if (carNumber == 0) {
            if (!railTracks.empty()) {
                std::cout << railTracks.front() << std::endl;
                railTracks.pop();
            }
        } else {
            railTracks.push(carNumber);
        }
    }

    return 0;
}
