#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 100

int main() {
    int t;
    while (scanf("%d", &t) == 1) {
        if (t == 0) break;
        int n, ok = 0, lunch = 0, dinner = 0, midnight = 0;
        scanf("%*d");
        for (int i = 0; i < t; i++) {
            char time[10], minute[10];
            scanf("%s %s", time, minute);
            int hour = atoi(time);
            int min = atoi(minute);
            if (hour >= 11 && hour <= 14) {
                lunch++;
                if (min <= 8) ok++;
            } else if (hour >= 18 && hour <= 20) {
                dinner++;
                if (min <= 8) ok++;
            } else if (hour >= 21 && hour <= 1) {
                midnight++;
                if (min <= 8) ok++;
            }
        }
        if (lunch == 0) printf("lunch no guest\n");
        else printf("lunch %.2f\n", (double)ok / lunch * 100);
        if (dinner == 0) printf("dinner no guest\n");
        else printf("dinner %.2f\n", (double)ok / dinner * 100);
        if (midnight == 0) printf("midnight no guest\n");
        else printf("midnight %.2f\n", (double)ok / midnight * 100);
    }
    return 0;
}
