#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 510
#define MAXM 510
#define MAXS 510

int n, m, s;
int p[MAXN][3];
int f[MAXN];
int g[MAXS][MAXN];
int h[MAXS][MAXN];
int vis[MAXS];

void dfs(int u) {
    if (vis[u]) return;
    vis[u] = 1;
    for (int i = 0; i < n; i++) {
        if (g[u][i] && !h[u][i]) {
            h[u][i] = 1;
            dfs(i);
        }
    }
}

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        scanf("%d", &n);
        for (int i = 0; i < n; i++) {
            scanf("%s %d %d", f[i], &p[i][0], &p[i][1]);
            p[i][2] = p[i][0] * 100 + p[i][1];
        }
        scanf("%d", &m);
        for (int i = 0; i < m; i++) {
            scanf("%s", f[i]);
            for (int j = 0; j < n; j++) {
                if (strcmp(f[i], f[j]) == 0) {
                    g[i][j] = 1;
                }
            }
        }
        s = 0;
        for (int i = 0; i < m; i++) {
            vis[i] = 0;
            for (int j = 0; j < n; j++) {
                h[i][j] = 0;
            }
        }
        for (int i = 0; i < m; i++) {
            if (!vis[i]) {
                dfs(i);
                s++;
            }
        }
        int ans = 0;
        for (int i = 0; i < m; i++) {
            if (vis[i]) {
                int cnt = 0;
                for (int j = 0; j < n; j++) {
                    if (g[i][j] && !h[i][j]) {
                        cnt++;
                    }
                }
                ans = ans > cnt ? ans : cnt;
            }
        }
        if (ans != m) {
            printf("-1\n");
        } else {
            printf("%d\n", ans);
        }
    }
    return 0;
}
