#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

struct Program {
    int id;
    int day;
    int start;
};

bool compare(Program a, Program b) {
    if (a.day != b.day) return a.day < b.day;
    return a.start < b.start;
}

int main() {
    int N, P;
    while (cin >> N) {
        if (N == 0) break;
        vector<Program> programs(N);
        for (int i = 0; i < N; i++) {
            string name;
            cin >> name;
            int day, start;
            cin >> day >> start;
            programs[i] = {i, day, start};
        }
        sort(programs.begin(), programs.end(), compare);
        cin >> P;
        vector<int> f(P);
        for (int i = 0; i < P; i++) {
            cin >> f[i];
            for (int j = 0; j < N; j++) {
                if (programs[j].id == f[i]) {
                    programs[j].id = -1;
                    break;
                }
            }
        }
        map<int, vector<int>> days;
        for (int i = 0; i < N; i++) {
            if (programs[i].id == -1) continue;
            days[programs[i].day].push_back(programs[i].start);
        }
        int ans = 0;
        for (auto& day : days) {
            sort(day.second.begin(), day.second.end());
            int j = 0;
            for (int i = 0; i < day.second.size(); i++) {
                while (j < i && day.second[j] + 30 <= day.second[i]) j++;
                ans = max(ans, i - j + 1);
            }
        }
        cout << ans << endl;
    }
    return 0;
}
