import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        StringBuilder exitCars = new StringBuilder();
        int currentCar = 0;

        while (scanner.hasNextLine()) {
            String input = scanner.nextLine();
            if (input.equals("0")) {
                exitCars.append(currentCar).append("\n");
            } else {
                currentCar = Integer.parseInt(input);
            }
        }

        System.out.println(exitCars.toString().trim());
    }
}
