#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int x1, y1, x2, y2;
} Frame;

int compare_frames(const void *a, const void *b) {
    Frame *frame_a = (Frame *)a;
    Frame *frame_b = (Frame *)b;

    if (frame_a->x2 == frame_b->x2) {
        return frame_a->y2 - frame_b->y2;
    } else {
        return frame_a->x2 - frame_b->x2;
    }
}

void read_frames(int n, Frame frames[]) {
    int i;
    for (i = 0; i < n; i++) {
        printf("%d\n", i + 1);
    }
    printf("\n");
}

int main() {
    int n, i;
    Frame frames[100];

    while (scanf("%d", &n) == 1) {
        if (n == 0) {
            break;
        }

        for (i = 0; i < n; i++) {
            scanf("%d %d %d %d", &frames[i].x1, &frames[i].y1, &frames[i].x2, &frames[i].y2);
        }

        qsort(frames, n, sizeof(Frame), compare_frames);

        int current_x = frames[n - 1].x2;
        int current_y = frames[n - 1].y2;
        int read_frames_count = 0;

        while (read_frames_count < n) {
            int i;
            for (i = n - 1; i >= 0; i--) {
                if (frames[i].x2 == current_x && frames[i].y2 > current_y) {
                    current_y = frames[i].y2;
                    read_frames_count++;
                }
            }

            int j;
            for (j = n - 1; j >= 0; j--) {
                if (frames[j].x2 == current_x && frames[j].y2 == current_y) {
                    current_x = frames[j].x1;
                    break;
                }
            }

            if (j < 0) {
                break;
            }
        }

        read_frames(n, frames);
        printf("\n");
    }

    return 0;
}
