import sys

def read_frames():
    n = int(input())
    frames = []
    for _ in range(n):
        x0, y0, x1, y1 = map(int, input().split())
        frames.append((x0, y0, x1, y1))
    return n, frames

def read_next_frame(frames, visited):
    max_x = -1
    max_y = -1
    next_frame = None
    for i, (x0, y0, x1, y1) in enumerate(frames):
        if not visited[i]:
            if x1 > max_x or (x1 == max_x and y0 < max_y):
                max_x = x1
                max_y = y0
                next_frame = i
    return next_frame

def draw_line(x0, y0, x1, y1, frames):
    visited = [False] * len(frames)
    visited[0] = True
    x = x1
    y = y0
    while x > 0:
        for i, (x0, y0, x1, y1) in enumerate(frames):
            if not visited[i] and x0 <= x <= x1 and y0 <= y <= y1:
                visited[i] = True
        x -= 1
        y = y0

def print_order(n, frames):
    visited = [False] * n
    order = 1
    while True:
        next_frame = read_next_frame(frames, visited)
        if next_frame is None:
            break
        print(next_frame + 1)
        visited[next_frame] = True
        draw_line(frames[next_frame][0], frames[next_frame][1], frames[next_frame][2], frames[next_frame][3], frames)
        order += 1

def main():
    while True:
        n, frames = read_frames()
        if n == 0:
            break
        print_order(n, frames)
        print()

if __name__ == "__main__":
    main()
