#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 105;

int n, m;
vector<int> g[MAXN];
int dp[MAXN][MAXN];

int dfs(int u, int p, int c) {
    for (int v : g[u]) {
        if (v == p) continue;
        if (dp[v][0] == -1) {
            dp[v][0] = c;
            dp[v][1] = 0;
            dp[v][2] = 1;
            dfs(v, u, c);
        }
        if (dp[v][1] == -1) {
            dp[v][1] = c;
            dp[v][2] = 0;
            dfs(v, u, c);
        }
        if (dp[v][2] == -1) {
            dp[v][2] = c;
            dp[v][0] = 0;
            dp[v][1] = 1;
            dfs(v, u, c);
        }
    }
    return 0;
}

int main() {
    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;
        for (int i = 0; i < n; i++) {
            g[i].clear();
            for (int j = 0; j < 3; j++) {
                dp[i][j] = -1;
            }
        }
        for (int i = 0; i < m; i++) {
            int u, v, c;
            cin >> u >> v >> c;
            g[u].push_back(v);
        }
        int ans = 1e9;
        for (int i = 0; i < n; i++) {
            if (dp[i][0] == -1) {
                dfs(i, -1, 0);
            }
            ans = min(ans, max(dp[i][0], dp[i][1]));
        }
        cout << ans << endl;
    }
    return 0;
}
