from collections import deque

def min_cost(n, m, edges):
    graph = [[] for _ in range(n)]
    for u, v, c in edges:
        graph[u].append((v, c))

    visited = [False] * n
    queue = deque()
    for i in range(n):
        if not visited[i]:
            queue.append(i)
            visited[i] = True
            while queue:
                u = queue.popleft()
                for v, c in graph[u]:
                    if not visited[v]:
                        queue.append(v)
                        visited[v] = True

    cost = 0
    for u, v, c in edges:
        if not visited[u] or not visited[v]:
            cost += c

    return cost

def main():
    while True:
        n, m = map(int, input().split())
        if n == 0 and m == 0:
            break
        edges = []
        for _ in range(m):
            u, v, c = map(int, input().split())
            edges.append((u, v, c))
        print(min_cost(n, m, edges))

if __name__ == "__main__":
    main()
