import sys
import math

def get_area(x1, y1, x2, y2):
    return abs((x1 * y2 - x2 * y1))

def get_intersection(x1, y1, x2, y2, x3, y3, x4, y4):
    denominator = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4)
    if denominator == 0:
        return None
    t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denominator
    u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denominator
    if 0 <= t <= 1 and 0 <= u <= 1:
        return (x1 + t * (x2 - x1), y1 + t * (y2 - y1))
    return None

def get_min_almonds(n, m, w, h, S, lines, almonds):
    lines.sort(key=lambda x: x[0])
    dp = [[float('inf')] * (m + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    for i in range(n):
        for j in range(m + 1):
            if dp[i][j] != float('inf'):
                for k in range(j + 1, m + 1):
                    intersection = get_intersection(0, lines[j][0], 0, lines[j][1], almonds[i][0], almonds[i][1], w, h)
                    if intersection is not None:
                        x, y = intersection
                        area = get_area(0, lines[j][0], 0, lines[j][1]) + get_area(x, y, w, h)
                        if area >= S:
                            dp[i + 1][k] = min(dp[i + 1][k], dp[i][j] + 1)
    min_almonds = float('inf')
    for i in range(m + 1):
        min_almonds = min(min_almonds, dp[n][i])
    return min_almonds

def main():
    input = sys.stdin.readline
    for _ in range(int(input())):
        n, m, w, h, S = map(int, input().split())
        lines = []
        for _ in range(m):
            lines.append(tuple(map(int, input().split())))
        almonds = []
        for _ in range(n):
            almonds.append(tuple(map(float, input().split())))
        print(get_min_almonds(n, m, w, h, S, lines, almonds))

if __name__ == "__main__":
    main()
