#include <stdio.h>
#include <stdlib.h>

#define MAX_SIZE 1000000

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int r, c, q;
    int grid[MAX_SIZE][MAX_SIZE];
    int subgrid[MAX_SIZE][MAX_SIZE];

    while (scanf("%d %d %d", &r, &c, &q) == 3) {
        for (int i = 0; i < r; i++) {
            for (int j = 0; j < c; j++) {
                scanf("%d", &grid[i][j]);
            }
        }

        for (int i = 0; i < r; i++) {
            for (int j = 0; j < c; j++) {
                subgrid[i][j] = grid[i][j];
            }
        }

        for (int k = 0; k < q; k++) {
            int r1, c1, r2, c2;
            scanf("%d %d %d %d", &r1, &c1, &r2, &c2);

            for (int i = r1; i <= r2; i++) {
                for (int j = c1; j <= c2; j++) {
                    subgrid[i][j] = min(subgrid[i][j], grid[i][j]);
                }
            }

            printf("%d\n", subgrid[r2][c2]);
        }
    }

    return 0;
}
