#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int t;
    cin >> t;
    while (t--) {
        int r, c, q;
        cin >> r >> c >> q;
        vector<vector<int>> grid(r, vector<int>(c));
        for (int i = 0; i < r; i++) {
            for (int j = 0; j < c; j++) {
                cin >> grid[i][j];
            }
        }
        while (q--) {
            int r1, c1, r2, c2;
            cin >> r1 >> c1 >> r2 >> c2;
            int minVal = INT_MAX;
            for (int i = r1; i <= r2; i++) {
                for (int j = c1; j <= c2; j++) {
                    minVal = min(minVal, grid[i][j]);
                }
            }
            cout << minVal << endl;
        }
    }
    return 0;
}
