import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int t = 1;
        while (t-- > 0) {
            int r = sc.nextInt();
            int c = sc.nextInt();
            int q = sc.nextInt();
            int[][] grid = new int[r][c];
            for (int i = 0; i < r; i++) {
                for (int j = 0; j < c; j++) {
                    grid[i][j] = sc.nextInt();
                }
            }
            for (int i = 0; i < q; i++) {
                int r1 = sc.nextInt();
                int c1 = sc.nextInt();
                int r2 = sc.nextInt();
                int c2 = sc.nextInt();
                int min = Integer.MAX_VALUE;
                for (int j = r1; j <= r2; j++) {
                    for (int k = c1; k <= c2; k++) {
                        min = Math.min(min, grid[j][k]);
                    }
                }
                out.println(min);
            }
        }
        out.close();
    }
}
