#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        int n;
        std::cin >> n;
        std::vector<long long> b(n * 2);
        for (int i = 0; i < n; i++) {
            std::cin >> b[i];
        }
        for (int i = n; i < n * 2; i++) {
            std::cin >> b[i];
        }

        std::vector<long long> a;
        a.push_back(2);
        for (int i = 0; i < n; i++) {
            long long x = b[i];
            long long y = b[n + i];
            long long z = std::sqrt(x * 1LL * y);
            while (z * z < x * 1LL * y) {
                z++;
            }
            while (z * z > x * 1LL * y) {
                z--;
            }
            a.push_back(z);
        }

        std::sort(a.begin(), a.end());
        std::cout << a[0] << std::endl;
        for (int i = 1; i < a.size(); i++) {
            std::cout << a[i] << " ";
        }
        std::cout << std::endl;
    }
    return 0;
}
