#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Anime {
    string str;
    int exp;
    int price;
};

bool compare(Anime a, Anime b) {
    return a.exp > b.exp;
}

int main() {
    int N, W, Q;
    cin >> N >> W >> Q;

    vector<Anime> animes;
    while (N--) {
        string str;
        int exp, price;
        cin >> str >> exp >> price;
        animes.push_back({str, exp, price});
    }

    sort(animes.begin(), animes.end(), compare);

    vector<vector<int>> dp(20, vector<int>(1 << 10, -1));
    for (int i = 0; i < animes.size(); i++) {
        for (int j = 0; j < 1 << animes[i].str.size(); j++) {
            if (j == 0) {
                dp[i][j] = animes[i].exp;
            } else {
                int k = 0;
                while (j >> k & 1) k++;
                dp[i][j] = max(dp[i][j], dp[i - 1][j - (1 << k)] + animes[i].exp);
            }
        }
    }

    while (Q--) {
        string query;
        cin >> query;
        int ans = 0;
        for (int i = 0; i < animes.size(); i++) {
            if (animes[i].str.find(query) != string::npos) {
                int j = 0;
                for (char c : query) {
                    if (animes[i].str.find(c) != string::npos) {
                        j |= 1 << (animes[i].str.find(c) - animes[i].str.find(query));
                    }
                }
                ans = max(ans, dp[i][j]);
            }
        }
        if (ans == -1) ans = 0;
        cout << ans << endl;
    }

    return 0;
}
