#include <iostream>
#include <cmath>

int main() {
    int d;
    while (std::cin >> d) {
        if (d == 0) {
            std::cout << 0 << std::endl;
        } else if (d > 600) {
            std::cout << 72000000 << std::endl;
        } else {
            double area = 0;
            for (int i = 1; i <= 600 / d; i++) {
                area += d * std::pow(i * d, 2);
            }
            std::cout << area << std::endl;
        }
    }
    return 0;
}
