#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

double calculateArea(vector<pair<int, int>> points) {
    int n = points.size();
    double area = 0.0;
    for (int i = 0; i < n; i++) {
        int x1 = points[i].first;
        int y1 = points[i].second;
        int x2 = points[(i + 1) % n].first;
        int y2 = points[(i + 1) % n].second;
        area += (x1 * y2 - x2 * y1);
    }
    return abs(area) / 2.0;
}

int main() {
    int t;
    cin >> t;
    int count = 1;
    while (t--) {
        int n;
        cin >> n;
        if (n == 0) break;
        vector<pair<int, int>> points(n);
        for (int i = 0; i < n; i++) {
            cin >> points[i].first >> points[i].second;
        }
        double area = calculateArea(points);
        cout << count << " " << fixed << setprecision(1) << area << endl;
        count++;
    }
    return 0;
}
