#include <iostream>
#include <string>
#include <vector>

int main() {
    int t;
    std::cin >> t;

    while (t--) {
        std::string s;
        std::cin >> s;

        int n = s.length();
        int m;
        std::cin >> m;

        std::vector<std::string> commands(m);
        for (int i = 0; i < m; i++) {
            std::cin >> commands[i];
        }

        int pos = 0;
        for (int i = 0; i < m; i++) {
            std::string command = commands[i];
            if (command == "forward char") {
                if (pos < n - 1) {
                    pos++;
                }
            } else if (command == "forward word") {
                int j = pos;
                while (j < n && s[j] != ' ') {
                    j++;
                }
                if (j < n) {
                    j++;
                }
                while (j < n && s[j] == ' ') {
                    j++;
                }
                pos = j;
            } else if (command == "backward char") {
                if (pos > 0) {
                    pos--;
                }
            } else if (command == "backward word") {
                int j = pos;
                while (j > 0 && s[j - 1] != ' ') {
                    j--;
                }
                if (j > 0) {
                    j--;
                }
                while (j > 0 && s[j - 1] == ' ') {
                    j--;
                }
                pos = j;
            } else if (command == "insert") {
                std::string arg;
                std::cin >> arg;
                s.insert(pos, arg);
                pos += arg.length();
            } else if (command == "delete char") {
                if (pos < n) {
                    s.erase(pos, 1);
                }
            } else if (command == "delete word") {
                int j = pos;
                while (j < n && s[j] != ' ') {
                    j++;
                }
                if (j < n) {
                    j++;
                }
                while (j < n && s[j] == ' ') {
                    j++;
                }
                if (j < n) {
                    s.erase(pos, j - pos);
                }
            }
        }

        for (int i = 0; i < n; i++) {
            if (i == pos) {
                std::cout << "^";
            }
            std::cout << s[i];
        }
        std::cout << std::endl;
    }

    return 0;
}
