import java.util.Scanner;

public class TextEditor {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        for (int i = 0; i < n; i++) {
            String text = scanner.nextLine();
            int m = scanner.nextInt();
            scanner.nextLine(); // Consume newline left-over

            String[] commands = new String[m];
            for (int j = 0; j < m; j++) {
                commands[j] = scanner.nextLine();
            }

            StringBuilder result = new StringBuilder(text);
            int cursorPosition = 0;

            for (String command : commands) {
                String[] parts = command.split(" ");
                String action = parts[0];

                switch (action) {
                    case "forward":
                        if (cursorPosition < text.length() - 1) {
                            cursorPosition++;
                        }
                        break;
                    case "backward":
                        if (cursorPosition > 0) {
                            cursorPosition--;
                        }
                        break;
                    case "insert":
                        String textToInsert = parts[1].replaceAll("\"", "");
                        result.insert(cursorPosition, textToInsert);
                        cursorPosition += textToInsert.length();
                        break;
                    case "delete":
                        if (parts[1].equals("char")) {
                            if (cursorPosition < text.length()) {
                                result.deleteCharAt(cursorPosition);
                            }
                        } else {
                            int wordStart = cursorPosition;
                            while (wordStart > 0 && !isSpace(result.charAt(wordStart - 1))) {
                                wordStart--;
                            }
                            int wordEnd = cursorPosition;
                            while (wordEnd < result.length() && !isSpace(result.charAt(wordEnd))) {
                                wordEnd++;
                            }
                            result.delete(wordStart, wordEnd);
                            cursorPosition = Math.max(0, cursorPosition - (wordEnd - wordStart));
                        }
                        break;
                    case "word":
                        if (cursorPosition < text.length()) {
                            int wordStart = cursorPosition;
                            while (wordStart > 0 && !isSpace(result.charAt(wordStart - 1))) {
                                wordStart--;
                            }
                            int wordEnd = cursorPosition;
                            while (wordEnd < result.length() && !isSpace(result.charAt(wordEnd))) {
                                wordEnd++;
                            }
                            if (wordStart == 0) {
                                cursorPosition = result.length();
                            } else {
                                cursorPosition = wordStart;
                            }
                        }
                        break;
                }
            }

            System.out.println(result.toString() + "^");
        }
    }

    private static boolean isSpace(char c) {
        return c == ' ' || c == ',' || c == '.' || c == '"';
    }
}
