def text_editor():
    for _ in range(int(input())):
        text = input()
        cursor = 0
        words = text.split()
        while True:
            command = input().split()
            if command[0] == 'forward':
                if command[1] == 'char':
                    if cursor < len(text) - 1:
                        cursor += 1
                elif command[1] == 'word':
                    while cursor < len(text) and text[cursor] != ' ':
                        cursor += 1
                    while cursor < len(text) and text[cursor] == ' ':
                        cursor += 1
                    while cursor < len(text) and text[cursor] != ' ':
                        cursor += 1
            elif command[0] == 'backward':
                if command[1] == 'char':
                    if cursor > 0:
                        cursor -= 1
                elif command[1] == 'word':
                    while cursor >= 0 and text[cursor] != ' ':
                        cursor -= 1
                    while cursor >= 0 and text[cursor] == ' ':
                        cursor -= 1
                    while cursor >= 0 and text[cursor] != ' ':
                        cursor -= 1
            elif command[0] == 'insert':
                text = text[:cursor] + command[1] + text[cursor:]
                cursor += len(command[1])
            elif command[0] == 'delete':
                if command[1] == 'char':
                    if cursor < len(text):
                        text = text[:cursor] + text[cursor + 1:]
                elif command[1] == 'word':
                    while cursor < len(text) and text[cursor] != ' ':
                        cursor += 1
                    while cursor < len(text) and text[cursor] == ' ':
                        cursor += 1
                    while cursor < len(text) and text[cursor] != ' ':
                        cursor += 1
                    while cursor >= 0 and text[cursor] != ' ':
                        cursor -= 1
                    while cursor >= 0 and text[cursor] == ' ':
                        cursor -= 1
                    while cursor >= 0 and text[cursor] != ' ':
                        cursor -= 1
                    text = text[:cursor] + text[cursor + 1:]
            print(text[:cursor] + '^')

text_editor()
