import re
import cmath

def calculate(expression):
    # Replace 'i' with '1j'
    expression = re.sub(r'i', 'j', expression)

    # Replace 'i' with '1j' in the expression
    expression = re.sub(r'(\d+)i', r'\1j', expression)

    # Replace 'i' with '1j' in the expression
    expression = re.sub(r'i', 'j', expression)

    try:
        # Evaluate the expression
        result = eval(expression)

        # Check for overflow
        if abs(result.real) > 10000 or abs(result.imag) > 10000:
            return "overflow"
        else:
            # Format the result
            if result.real == 0 and result.imag == 0:
                return "0"
            elif result.real == 0:
                return f"{result.imag:+}i"
            elif result.imag == 0:
                return f"{result.real:+}"
            else:
                return f"{result.real:+}{result.imag:+}i"

    except OverflowError:
        return "overflow"

def main():
    while True:
        expression = input()
        if expression == "":
            break
        print(calculate(expression))

if __name__ == "__main__":
    main()
