#include <iostream>
#include <vector>
#include <algorithm>
#include <string>

using namespace std;

const int N = 4;
const int M = 4;
const int K = 24;

struct Position {
    int x, y;
};

bool operator<(const Position& a, const Position& b) {
    if (a.x != b.x) return a.x < b.x;
    return a.y < b.y;
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        vector<Position> pos(K);
        for (int i = 0; i < K; i++) {
            cin >> pos[i].x >> pos[i].y;
        }
        sort(pos.begin(), pos.end());
        int ans = 1;
        for (int i = 0; i < K; i++) {
            for (int j = i + 1; j < K; j++) {
                if (pos[i].x == pos[j].x && pos[i].y == pos[j].y) {
                    ans = 0;
                    break;
                }
            }
            if (ans == 0) break;
        }
        if (ans == 0) {
            cout << 0 << endl;
            continue;
        }
        for (int i = 0; i < K; i++) {
            for (int j = i + 1; j < K; j++) {
                if (pos[i].x == pos[j].x && pos[i].y == pos[j].y) {
                    ans *= 2;
                    break;
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}
