#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_COMMAND 50
#define MAX_FIELD_SIZE 100

typedef struct {
    int x, y;
    int dir;
} Robot;

void init(Robot *r) {
    r->x = 1;
    r->y = 1;
    r->dir = 0; // 0: north, 1: east, 2: south, 3: west
}

void move(Robot *r, int k) {
    switch (r->dir) {
        case 0: // north
            if (r->y + k <= MAX_FIELD_SIZE) {
                r->y += k;
            } else {
                r->y = MAX_FIELD_SIZE - r->y + k;
            }
            break;
        case 1: // east
            if (r->x + k <= MAX_FIELD_SIZE) {
                r->x += k;
            } else {
                r->x = MAX_FIELD_SIZE - r->x + k;
            }
            break;
        case 2: // south
            if (r->y - k >= 1) {
                r->y -= k;
            } else {
                r->y = k - r->y + 1;
            }
            break;
        case 3: // west
            if (r->x - k >= 1) {
                r->x -= k;
            } else {
                r->x = k - r->x + 1;
            }
            break;
    }
}

int main() {
    char command[MAX_COMMAND];
    int m, n;
    Robot r;

    while (scanf("%d %d", &m, &n) == 2) {
        if (m == 0 && n == 0) {
            break;
        }

        init(&r);

        while (1) {
            scanf("%s", command);
            if (strcmp(command, "STOP") == 0) {
                break;
            }

            int k;
            if (sscanf(command, "FORWARD %d", &k) == 1) {
                move(&r, k);
            } else if (strcmp(command, "BACKWARD") == 0) {
                scanf("%d", &k);
                move(&r, -k);
            } else if (strcmp(command, "RIGHT") == 0) {
                r.dir = (r.dir + 1) % 4;
            } else if (strcmp(command, "LEFT") == 0) {
                r.dir = (r.dir - 1 + 4) % 4;
            }
        }

        printf("%d %d\n", r.x, r.y);
    }

    return 0;
}
