#include <iostream>
#include <string>
#include <sstream>

using namespace std;

int main() {
    int m, n;
    string line;
    while (true) {
        getline(cin, line);
        if (line == "0 0") break;
        istringstream iss(line);
        iss >> m >> n;

        int x = 1, y = 1;
        string command;
        while (true) {
            getline(cin, line);
            if (line == "STOP") break;
            istringstream iss(line);
            iss >> command;
            if (command == "FORWARD" || command == "BACKWARD") {
                int k;
                iss >> k;
                if (command == "FORWARD") {
                    if (y + k <= n) y += k;
                    else if (y - k >= 1) y -= k;
                    else {
                        y = y - k + 1;
                        cout << x << " " << y << endl;
                        break;
                    }
                } else {
                    if (y - k >= 1) y -= k;
                    else if (y + k <= n) y += k;
                    else {
                        y = y + k - 1;
                        cout << x << " " << y << endl;
                        break;
                    }
                }
                if (x + k <= m) x += k;
                else if (x - k >= 1) x -= k;
                else {
                    x = x - k + 1;
                    cout << x << " " << y << endl;
                    break;
                }
            } else if (command == "RIGHT" || command == "LEFT") {
                if (command == "RIGHT") {
                    if (x == 1) x = m;
                    else x--;
                } else {
                    if (x == m) x = 1;
                    else x++;
                }
                if (command == "RIGHT") {
                    if (y == 1) y = n;
                    else y--;
                } else {
                    if (y == n) y = 1;
                    else y++;
                }
            }
        }
        cout << x << " " << y << endl;
    }
    return 0;
}
