import java.util.Scanner;

public class Robot {
    private int x, y, m, n;

    public Robot(int x, int y, int m, int n) {
        this.x = x;
        this.y = y;
        this.m = m;
        this.n = n;
    }

    public void moveForward(int k) {
        if (y + k <= n) {
            y += k;
        } else {
            y = n - (n - y);
        }
    }

    public void moveBackward(int k) {
        if (y - k >= 1) {
            y -= k;
        } else {
            y = 1;
        }
    }

    public void turnRight() {
        if (x == 1) {
            x = m;
        } else {
            x--;
        }
    }

    public void turnLeft() {
        if (x == m) {
            x = 1;
        } else {
            x++;
        }
    }

    public void executeCommand(String command) {
        String[] parts = command.split(" ");
        switch (parts[0]) {
            case "FORWARD":
                moveForward(Integer.parseInt(parts[1]));
                break;
            case "BACKWARD":
                moveBackward(Integer.parseInt(parts[1]));
                break;
            case "RIGHT":
                turnRight();
                break;
            case "LEFT":
                turnLeft();
                break;
            case "STOP":
                return;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            String[] parts = scanner.nextLine().split(" ");
            if (parts[0].equals("0") && parts[1].equals("0")) {
                break;
            }
            int m = Integer.parseInt(parts[0]);
            int n = Integer.parseInt(parts[1]);
            Robot robot = new Robot(1, 1, m, n);
            while (true) {
                String command = scanner.nextLine();
                if (command.equals("STOP")) {
                    break;
                }
                robot.executeCommand(command);
            }
            System.out.println(robot.x + " " + robot.y);
        }
    }
}
