import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[] heights = new int[10];

        for (int i = 0; i < 10; i++) {
            System.out.print("Enter height of mountain " + (i + 1) + ": ");
            heights[i] = scanner.nextInt();
        }

        Arrays.sort(heights);
        for (int i = 9; i > 9 - 3; i--) {
            System.out.println("Height of the " + (i - 8 + 1) + "st mountain: " + heights[i]);
        }
    }
}
