#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

vector<int> factorize(int a, int b, int c) {
    for (int p = 1; p <= a; ++p) {
        if (a % p == 0) {
            int q = (b * p) / a;
            int r = a / p;
            int s = c / (a / p);
            if (q * r == b && p > 0 && r > 0 && (p > r || p == r && q >= s)) {
                return {p, q, r, s};
            }
        }
    }
    return {};
}

int main() {
    int a, b, c;
    while (cin >> a >> b >> c) {
        if (a == 0) {
            cout << "Impossible" << endl;
        } else {
            vector<int> factors = factorize(a, b, c);
            if (factors.empty()) {
                cout << "Impossible" << endl;
            } else {
                for (int factor : factors) {
                    cout << factor << " ";
                }
                cout << endl;
            }
        }
    }
    return 0;
}
