import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int a = scanner.nextInt();
            int b = scanner.nextInt();
            int c = scanner.nextInt();
            if (a == 0 && b == 0 && c == 0) {
                break;
            }
            if (factorize(a, b, c)) {
                System.out.println(factorize(a, b, c));
            } else {
                System.out.println("Impossible");
            }
        }
    }

    public static boolean factorize(int a, int b, int c) {
        for (int p = 1; p <= a; p++) {
            if (a % p != 0) {
                continue;
            }
            for (int q = 1; q <= c; q++) {
                if (c % q != 0) {
                    continue;
                }
                int r = b / (p * q - c);
                int s = (p * q - c) / b * r;
                if (r > 0 && s > 0 && (p > r || (p == r && q >= s))) {
                    return true;
                }
            }
        }
        return false;
    }
}
