#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

struct Point {
    int x, y;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p1.x - p2.x, 2) + std::pow(p1.y - p2.y, 2));
}

double angle(const Point& p1, const Point& p2) {
    return std::atan2(p2.y - p1.y, p2.x - p1.x);
}

int main() {
    int n;
    while (std::cin >> n) {
        if (n == 0) break;

        std::vector<Point> points(n);
        for (int i = 0; i < n; ++i) {
            std::cin >> points[i].x >> points[i].y;
        }

        std::vector<Point> visited;
        visited.push_back({0, 0});
        double totalDistance = 0;

        int direction = 0; // 0: north, 1: east, 2: south, 3: west
        Point current = {0, 0};
        while (!visited.empty()) {
            double minAngle = 2 * M_PI;
            Point nextPoint;

            for (const auto& point : points) {
                if (std::find(visited.begin(), visited.end(), point) == visited.end()) {
                    double angleToNext = angle(current, point);
                    if (direction == 0) {
                        if (angleToNext < minAngle && angleToNext > -M_PI) {
                            minAngle = angleToNext;
                            nextPoint = point;
                        }
                    } else if (direction == 1) {
                        if (angleToNext > minAngle && angleToNext < M_PI) {
                            minAngle = angleToNext;
                            nextPoint = point;
                        }
                    } else if (direction == 2) {
                        if (angleToNext < minAngle && angleToNext < -M_PI) {
                            minAngle = angleToNext;
                            nextPoint = point;
                        }
                    } else if (direction == 3) {
                        if (angleToNext > minAngle && angleToNext > -M_PI) {
                            minAngle = angleToNext;
                            nextPoint = point;
                        }
                    }
                }
            }

            visited.push_back(nextPoint);
            totalDistance += distance(current, nextPoint);
            current = nextPoint;

            if (direction == 0 && nextPoint.x == current.x) {
                direction = 1;
            } else if (direction == 1 && nextPoint.y == current.y) {
                direction = 2;
            } else if (direction == 2 && nextPoint.x == current.x) {
                direction = 3;
            } else if (direction == 3 && nextPoint.y == current.y) {
                direction = 0;
            }
        }

        std::cout << std::fixed << std::setprecision(1) << totalDistance << std::endl;
    }

    return 0;
}
