#include <iostream>
#include <string>
#include <cmath>

std::string add(const std::string& a, const std::string& b) {
    if (a.length() + b.length() > 80) {
        return "overflow";
    }

    std::string result;
    int carry = 0;
    int i = a.length() - 1, j = b.length() - 1;

    while (i >= 0 || j >= 0) {
        int sum = carry;
        if (i >= 0) {
            sum += a[i--] - '0';
        }
        if (j >= 0) {
            sum += b[j--] - '0';
        }

        result += std::to_string(sum % 10);
        carry = sum / 10;
    }

    if (carry > 0) {
        result += std::to_string(carry);
    }

    std::reverse(result.begin(), result.end());
    return result;
}

int main() {
    int n;
    std::cin >> n;

    for (int i = 0; i < n; i++) {
        std::string a, b;
        std::cin >> a >> b;
        std::cout << add(a, b) << std::endl;
    }

    return 0;
}
