import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def angle(p1, p2):
    return math.atan2(p2[1] - p1[1], p2[0] - p1[0])

def next_flag(flags, current, direction):
    min_angle = float('inf')
    next = None
    for flag in flags:
        if flag == current:
            continue
        angle_to_flag = abs(angle(current, flag) - direction)
        if angle_to_flag < min_angle:
            min_angle = angle_to_flag
            next = flag
    return next

def spiral_course_length(flags):
    current = (0, 0)
    direction = math.pi / 2
    course = []
    while flags:
        next = next_flag(flags, current, direction)
        course.append((current, next))
        flags.remove(next)
        current = next
        direction = (direction + math.pi / 2) % (2 * math.pi)
    return sum(distance(p1, p2) for p1, p2 in course)

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        flags = []
        for _ in range(n):
            x, y = map(int, input().split())
            flags.append((x, y))
        print(round(spiral_course_length(flags), 1))

if __name__ == "__main__":
    main()
