from collections import defaultdict

def dfs(graph, start, visited, path, max_path):
    visited[start] = True
    path.append(start)
    for neighbor in graph[start]:
        if not visited[neighbor[0]]:
            dfs(graph, neighbor[0], visited, path, max_path)
    max_path[0] = max(max_path[0], len(path))
    if max_path[0] == len(path):
        max_path[1].append(tuple(sorted(path)))
    path.pop()

def longest_path(graph):
    n = len(graph)
    visited = [False] * n
    max_path = [0, []]
    for i in range(n):
        if not visited[i]:
            dfs(graph, i, visited, [], max_path)
    max_path[1].sort()
    return max_path[0], ' '.join(map(str, max_path[1][0]))

def main():
    n, _ = map(int, input().split())
    graph = defaultdict(list)
    while n != 0:
        n, _ = map(int, input().split())
        for _ in range(n):
            u, v, d = map(int, input().split())
            graph[u-1].append((v-1, d))
            graph[v-1].append((u-1, d))
        print(longest_path(graph))
        graph.clear()
        n, _ = map(int, input().split())

if __name__ == "__main__":
    main()
