#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int getMinPenalty(vector<vector<int>>& layout) {
    int penalty = 0;
    while (true) {
        bool found = false;
        for (int i = 0; i < 5; ++i) {
            for (int j = 0; j < 4; ++j) {
                if (j < 3 && layout[i][j] == layout[i][j + 1] && layout[i][j] != 0) {
                    found = true;
                    layout[i][j] = 0;
                    layout[i][j + 1] = 0;
                    penalty += 2;
                }
                if (i < 3 && layout[i][j] == layout[i + 1][j] && layout[i][j] != 0) {
                    found = true;
                    layout[i][j] = 0;
                    layout[i + 1][j] = 0;
                    penalty += 2;
                }
            }
        }
        if (!found) break;
        for (int i = 0; i < 5; ++i) {
            for (int j = 0; j < 4; ++j) {
                if (layout[i][j] != 0) {
                    int k = j;
                    while (k > 0 && layout[i][k - 1] == 0) --k;
                    if (k > 0) {
                        swap(layout[i][k], layout[i][j]);
                    }
                }
            }
        }
    }
    for (int i = 0; i < 5; ++i) {
        for (int j = 0; j < 4; ++j) {
            if (layout[i][j] != 0) {
                return penalty + 20 - layout[i][j];
            }
        }
    }
    return penalty;
}

int main() {
    int n;
    cin >> n;
    for (int i = 0; i < n; ++i) {
        vector<vector<int>> layout(5, vector<int>(4));
        for (int j = 0; j < 5; ++j) {
            for (int k = 0; k < 4; ++k) {
                cin >> layout[j][k];
            }
        }
        cout << getMinPenalty(layout) << endl;
    }
    return 0;
}
