from collections import defaultdict

def find_pairs(layout):
    pairs = defaultdict(list)
    for i in range(20):
        for j in range(i + 1, 20):
            if layout[i] == layout[j] and (i // 4, i % 4) != (j // 4, j % 4):
                pairs[layout[i]].append((i, j))
    return pairs

def remove_pair(layout, pairs):
    for value, pair_list in pairs.items():
        for pair in pair_list:
            i, j = pair
            layout[i], layout[j] = None, None
            return layout, pairs, True
    return layout, pairs, False

def shift_cards(layout):
    new_layout = [None] * 20
    for i in range(20):
        if layout[i] is not None:
            new_layout[i] = layout[i]
    return new_layout

def solve(layout):
    pairs = find_pairs(layout)
    while True:
        layout, pairs, removed = remove_pair(layout, pairs)
        if not removed:
            break
        layout = shift_cards(layout)
    return sum(1 for card in layout if card is not None)

def main():
    N = int(input())
    for _ in range(N):
        layout = []
        for _ in range(5):
            row = list(map(int, input().split()))
            layout.extend(row)
        print(solve(layout))

if __name__ == "__main__":
    main()
