#include <stdio.h>
#include <string.h>
#include <ctype.h>

#define MAX_RULES 1024
#define MAX_PACKETS 1024
#define MAX_ADDR 9
#define MAX_MSG 51

typedef struct {
    char src[MAX_ADDR];
    char dst[MAX_ADDR];
    char msg[MAX_MSG];
} Packet;

typedef struct {
    char keyword[10];
    char src[MAX_ADDR];
    char dst[MAX_ADDR];
} Rule;

int match(const char *addr, const char *pattern) {
    for (int i = 0; i < MAX_ADDR; i++) {
        if (addr[i] != pattern[i] && pattern[i] != '?') {
            return 0;
        }
    }
    return 1;
}

int check_rule(const Rule *rule, const Packet *packet) {
    return (strcmp(rule->keyword, "permit") == 0 && match(packet->src, rule->src) && match(packet->dst, rule->dst)) ||
           (strcmp(rule->keyword, "deny") == 0 && match(packet->src, rule->src) && match(packet->dst, rule->dst));
}

int main() {
    int n, m;
    Rule rules[MAX_RULES];
    Packet packets[MAX_PACKETS];

    while (scanf("%d %d", &n, &m) == 2) {
        if (n == 0 && m == 0) {
            break;
        }

        for (int i = 0; i < n; i++) {
            scanf("%s %s", rules[i].keyword, rules[i].src);
            scanf("%s", rules[i].dst);
        }

        for (int i = 0; i < m; i++) {
            scanf("%s %s %s", packets[i].src, packets[i].dst, packets[i].msg);
        }

        int count = 0;
        for (int i = 0; i < m; i++) {
            int valid = 0;
            for (int j = 0; j < n; j++) {
                if (check_rule(&rules[j], &packets[i])) {
                    valid = 1;
                    break;
                }
            }
            if (valid) {
                count++;
            }
        }

        printf("%d\n", count);
        for (int i = 0; i < m; i++) {
            int valid = 0;
            for (int j = 0; j < n; j++) {
                if (check_rule(&rules[j], &packets[i])) {
                    valid = 1;
                    break;
                }
            }
            if (valid) {
                printf("%s %s %s\n", packets[i].src, packets[i].dst, packets[i].msg);
            }
        }

        printf("\n");
    }

    return 0;
}
