#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Rule {
    string keyword;
    string source;
    string destination;
};

struct Packet {
    string source;
    string destination;
    string message;
};

bool match(const string& pattern, const string& address) {
    if (pattern.length() != address.length()) return false;
    for (int i = 0; i < pattern.length(); i++) {
        if (pattern[i] != '?' && pattern[i] != address[i]) return false;
    }
    return true;
}

bool checkRule(const Rule& rule, const Packet& packet) {
    return (rule.keyword == "permit" && match(rule.source, packet.source) && match(rule.destination, packet.destination)) ||
           (rule.keyword == "deny" && match(rule.source, packet.source) && match(rule.destination, packet.destination));
}

int main() {
    int n, m;
    while (cin >> n >> m) {
        if (n == 0 && m == 0) break;
        vector<Rule> rules;
        for (int i = 0; i < n; i++) {
            Rule rule;
            cin >> rule.keyword;
            cin >> rule.source >> rule.destination;
            rules.push_back(rule);
        }
        vector<Packet> packets;
        for (int i = 0; i < m; i++) {
            Packet packet;
            cin >> packet.source >> packet.destination >> packet.message;
            packets.push_back(packet);
        }
        vector<Packet> legalPackets;
        for (const auto& packet : packets) {
            bool isLegal = false;
            for (int i = 0; i < rules.size(); i++) {
                if (checkRule(rules[i], packet)) {
                    isLegal = true;
                    break;
                }
            }
            if (isLegal) legalPackets.push_back(packet);
        }
        cout << legalPackets.size() << endl;
        for (const auto& packet : legalPackets) {
            cout << packet.source << " " << packet.destination << " " << packet.message << endl;
        }
    }
    return 0;
}
